<!-- Tabs -->
<div id="tabs">
	<ul>
		<li><a href="#tabs-1">ABOUT ISAAC</a></li>
		<li><a href="#tabs-2">CONFERENCES</a></li>
		<li><a href="#tabs-3">WORKSHOPS</a></li>
      	<li><a href="#tabs-4">CONTESTS</a></li>
      	<li><a href="#tabs-5">EXHIBITIONS</a></li>
